import Script from './Script';
import {
  b2Vec2,
  b2BodyDef,
  b2FixtureDef,
  b2CircleShape,
  b2PolygonShape,
  b2Body,
  b2Fixture
} from '../utils/box2d';

const ShapeType = {
  CIRCLE: 'circle',
  POLYGON: 'polygon'
};

const BodyType = {
  STATIC: 'static',
  DYNAMIC: 'dynamic',
  KINEMATIC: 'kinematic'
};

export default class PhysicsBody extends Script {

  static factory() {
    return new PhysicsBody();
  }

  static get propsTypes() {
    return {
      ...Script.propsTypes,
      applyPositionTo: 'string_null',
      applyRotationTo: 'string_null',
      applyPositionFrom: 'string_null',
      applyRotationFrom: 'string_null',
      bodyType: 'enum(static, dynamic, kinematic)',
      radius: 'number',
      vertices: 'array(number)',
      shapeType: 'enum(circle, polygon)',
      density: 'number',
      friction: 'number',
      restitution: 'number',
      linearVelocity: 'vec2',
      angularVelocity: 'number',
      linearDamping: 'number',
      angularDamping: 'number',
      fixedRotation: 'boolean',
      bullet: 'boolean',
      sensor: 'boolean',
      coordsScale: 'number',
      listenForContacts: 'boolean'
